import React,{useState, useContext, useEffect} from 'react'
import AlertContext from "../../context/alert/alertContext";
import AuthContext from '../../context/auth/AuthContext';

const Register = () => {
    const alertContext = useContext(AlertContext);
    const authContext = useContext(AuthContext)

    const { setAlert } = alertContext;
    const {register, error, clearErrors} = authContext

    useEffect(()=>{
        if(error === 'User already exits'){
            setAlert(error, 'danger');
            clearErrors();

        }
    },[error]);

const [user, setUser]=useState({
    name:'',
    email:'',
    password:'',
    password2:''
})
const{name, email, password, password2} = user;

const onChange = e => setUser({...user,[e.target.name]: e.target.value})
const onSubmit = e => {
    e.preventDefault();
    if(name==='' || email===''|| password===''){
        setAlert('Please enter all fields', 'danger')

    }
    else if(password !==password2){
        setAlert(' Password does not match', 'danger')
    }
    else{
        register({
            name,
            email,
            password
        });
    }

};

    return (
       <div className="form-container">
           <h1>Acount <span className="text-primary">Register</span></h1>
           <form onSubmit={onSubmit}>
               <div className="form-group">
                   <label htmlFor="name">Name</label>
                   <input type="text" name='name' vlaue={name} onChange={onChange} required/>
               </div>
               <div className="form-group">
                   <label htmlFor="email">Email Address</label>
                   <input type="email" name='email' vlaue={email} onChange={onChange} required/>
               </div>
               <div className="form-group">
                   <label htmlFor="password">Password</label>
                   <input type="password" name='password' vlaue={password} onChange={onChange} required minLength='6'/>
               </div>
               <div className="form-group">
                   <label htmlFor="password">Confirm Password</label>
                   <input type="password" name='password2' vlaue={password2} onChange={onChange} required minLength='6'/>
               </div>
               <input type="submit" value="Register" className='btn btn-primary btn-block'/>
           </form>
       </div>
    )
}

export default Register
