import React,{useState, useContext} from 'react'
import AlertContext from "../../context/alert/alertContext";

const Register = () => {
    const alertContext = useContext(AlertContext)
    const {setAlert} = alertContext

const [user, setUser]=useState({
    name:'',
    email:'',
    password:'',
    password2:''
})
const{name, email, password, password2}=user;

const onChnage = e => setUser({...user,[e.target.name]:e.target.value})
const onSubmit = e => {
    e.preventDefault();
    if(name==='' || email===''|| password===''){
        setAlert('Please enter all fields', 'danger')

    }
    else if(password !==password2){
        setAlert(' Password does not match', 'danger')
    }
    console.log('Register submit')
}

    return (
       <div className="form-container">
           <h1>Acount <span className="text-primary">Register</span></h1>
           <form onSubmit={onSubmit}>
               <div className="form-group">
                   <label htmlFor="name">Name</label>
                   <input type="text" name='name' vlaue={name} onChange={onchange} required/>
               </div>
               <div className="form-group">
                   <label htmlFor="email">Email Address</label>
                   <input type="email" name='email' vlaue={email} onChange={onchange} required/>
               </div>
               <div className="form-group">
                   <label htmlFor="password">Password</label>
                   <input type="password" name='password' vlaue={password} onChange={onchange} required/>
               </div>
               <div className="form-group">
                   <label htmlFor="password">Confirm Password</label>
                   <input type="password" name='password2' vlaue={password2} onChange={onchange} required/>
               </div>
               <input type="submit" value="Register" className='btn btn-primary btn-block'/>
           </form>
       </div>
    )
}

export default Register
